const val START_OF_PACKET_MARKER_LENGTH = 4
const val START_OF_MESSAGE_MARKER_LENGTH = 14

fun main() {    
    val startOfPacket = inputString.findStartMarker(START_OF_PACKET_MARKER_LENGTH)
    val startOfMessage = inputString.findStartMarker(START_OF_MESSAGE_MARKER_LENGTH)
    
    println("Packet start detected after character ${startOfPacket}")
    println("Message start detected after character ${startOfMessage}")
}

fun String.findStartMarker(length: Int) = windowed(length).withIndex().first { it.value.isStartMarker(length) }.index + length
fun String.isStartMarker(length: Int) = asIterable().distinct().size == length

val inputString = ""
